import React, { useState } from 'react';
import { useSelector } from 'react-redux';

function StanleyCupSim() {
    const teams = useSelector((state) => state.teams);
    const [selectedTeam, setSelectedTeam] = useState("TOR");

    const selectTeam = (teamAbbrev) => {
        setSelectedTeam(teamAbbrev);  
      };
    
    const runSim = (selectedTeam) => {
        // grab the selected team's win %, which exists as a property in the json (winPctg)
        const selectedTeamWinPctg = teams.find(team => team.teamAbbrev.default === selectedTeam).winPctg;
    
        let wins = 0;
        let losses = 0;
        let gameResults = []; // Array to store the results of each game
    
        // loop over the list of teams and if the team isn't the selected team, "play a game"
        teams.forEach(team => {
            if (team.teamAbbrev.default !== selectedTeam) {
                // "play a game" means to take the selected team's win % minus the opponents win % and add that to 50%
                const winProbability = 50 + (selectedTeamWinPctg - team.winPctg);
    
                // then get a random whole number from 0-100. If the calculated win % is greater than the random number, record a win
                const randomNum = Math.floor(Math.random() * 101);
                let result;
                if (winProbability > randomNum) {
                    wins++;
                    result = 'win';
                } else {
                    // else, record a loss
                    losses++;
                    result = 'loss';
                }
    
                // Record the calculated win % and the result of the game
                gameResults.push({
                    opponent: team.teamAbbrev.default,
                    winProbability: winProbability,
                    result: result
                });
            }
        });
    
        console.log(`Wins: ${wins}, Losses: ${losses}`);
        console.log(gameResults); // Log the results of each game
    }
    

    return (
        <div className="content">
            <h1>Welcome to the Stanley Cup Simulator!</h1>
            <p>Note: This isn't a proper Stanley Cup simulation at the moment. Instead, your selected team faces off once against all other teams and the "season" results are posted back to you.</p>
            <div className="team-info">
                <div className="details">
                    <h2>Choose a Team!</h2>
                    <ul>
                        {teams.map(team => (
                            <li key={team.teamAbbrev.default} className="team" onClick={() => selectTeam(team.teamAbbrev.default)}><img className="team-icon" src={team.teamLogo} alt="team logo"></img>{team.teamName.default} ({team.teamAbbrev.default})<span className="arrow"> â†—</span></li>
                        ))}
                    </ul>
                </div>
                <div className="details">
                    <h2>Selected Team</h2>
                    <p>{selectedTeam}</p>
                    <button onClick={() => runSim(selectedTeam)}>Run Simulation!</button>
                </div>
            </div>
        </div>
    )
}

export default StanleyCupSim;
