import React, { useEffect, useState } from 'react';
import './teams.css';
import { useNavigate } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import {urlConfig} from '../../config';

function Teams() {
    const [teams, setTeams] = useState([]);
    const [isLoading, setIsLoading] = useState(true); // Add a loading state
    const navigate = useNavigate();
    

    useEffect(() => {
      // fetch all teams
      const fetchTeams = async () => {
          try {
              //let url = '${urlConfig.backendUrl}/api/teams';
              let url = 'http://localhost:3060/api/teams';
              const response = await fetch(url);
              if (!response.ok) {
                  //something went wrong
                  throw new Error(`HTTP error; ${response.status}`)
              }
              const data = await response.json();
              console.log(data);
              if(data) {
                console.log("SETTING TEAMS");
                setTeams(data);
              }
              setIsLoading(false); // Set loading to false after data is fetched
          } catch (error) {
              console.log('Fetch error: ' + error.message);
          }
      };

      console.log("CALLING FETCHTEAMS()");
      fetchTeams();
  }, []);

  const goToDetailsPage = (teamId) => {
    navigate(`/app/teams/${teamId}`); // TODO: make this route mean something
  }; 
  

    // Conditionally render based on loading state
    if (isLoading) {
      return <div>Loading...</div>;
  } else {
    var metro, atlantic, central, pacific = [];
    for(var i=0; i<teams.length; i++) {
      if(teams[i].divisionName == "Metropolitan") {
        metro.push(teams[i]);
      }
      else if(teams[i].divisionName == "Atlantic") {
        atlantic.push(teams[i]);
      }
      else if(teams[i].divisionName == "Central") {
        central.push(teams[i]);
      }
      else if(teams[i].divisionName == "Pacific") {
        pacific.push(teams[i]);
      }
      else {
        console.log("Unknown division: " + teams[i].divisionName); // TODO log to error
      }
    }
      return (
        <>
          <h1>Teams</h1>
          <ul>
          {metro.map(team => (
              <li key={team.teamAbbrev.default} class="team" onClick={() => goToDetailsPage(team.teamAbbrev.default)}>{team.teamName.default} ({team.teamAbbrev.default}) - {team.divisionName}</li>
            ))}
          {atlantic.map(team => (
              <li key={team.teamAbbrev.default} class="team" onClick={() => goToDetailsPage(team.teamAbbrev.default)}>{team.teamName.default} ({team.teamAbbrev.default}) - {team.divisionName}</li>
            ))}
          {central.map(team => (
            <li key={team.teamAbbrev.default} class="team" onClick={() => goToDetailsPage(team.teamAbbrev.default)}>{team.teamName.default} ({team.teamAbbrev.default}) - {team.divisionName}</li>
          ))}
          {pacific.map(team => (
            <li key={team.teamAbbrev.default} class="team" onClick={() => goToDetailsPage(team.teamAbbrev.default)}>{team.teamName.default} ({team.teamAbbrev.default}) - {team.divisionName}</li>
          ))}
          </ul>
        </>
      );
  }
}

export default Teams;
