import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';


function StanleyCupSim() {
    const teams = useSelector((state) => state.teams);

    return (
        <>
            <h1>Welcome to the Stanley Cup Simulator!</h1>
            <h2>How It Works</h2>
            <p>
                Select your team from the list of available teams. Once you have your desired team selected, click "Simulate Tourney" below.
                Then behind the scenes a tournament simulation will be run using both teams' win percent.
            </p>
            <aside>How scores are calculated: (TODO)</aside>

            <ul>
                {teams.map(team => (
                    <li key={team.teamAbbrev.default} class="team"><img class="team-icon" src={team.teamLogo} alt="team logo"></img>{team.teamName.default} ({team.teamAbbrev.default})<span class="arrow"> â†—</span></li>
                ))}
              </ul>
        </>
    )
}

export default StanleyCupSim;
