import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './TeamDetails.css';

function TeamDetails() {
    const navigate = useNavigate();
    const { teamId } = useParams();
    const [team, setTeam] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // get the team to be rendered on the details page
        const fetchTeam = async () => {
            try {
                let url = `http://localhost:3060/api/teams/${teamId}`;
                const response = await fetch(url);
                if(response.status == 404) {
                    throw new Error('Team not found.');
                }
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setTeam(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchTeam();
    }, [teamId, navigate]);

    const handleBackClick = () => {
        navigate(-1);
    };

    if (loading) return <><h1>Loading...</h1><button onClick={handleBackClick}>Back</button></>;
    if (error) return <><h1>Error: {error}</h1><button onClick={handleBackClick}>Back</button></>;
    if (!team) return <><h1>Team not found</h1><button onClick={handleBackClick}>Back</button></>;

    return (
        <>
            <h1>{team.teamFullName} Details</h1>
            <button onClick={handleBackClick}>Back</button>
            <div class="team-info">
                <div class="details">
                    <ul>
                        <li key={team.teamAbbrev.default}>{team.teamName.default} in {team.conferenceName} Conference</li>
                    </ul>
                </div>
                <div class="img-area">
                    <img class="team-logo" src={team.teamLogo} alt="team logo"></img>
                </div>
            </div>
        </>
      );
}

export default TeamDetails;
